import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 40,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
    }
    componentDidMount(){
        this.setState({
            productItemObjects :  Array.from(Array(this.state.productCount), () => <ProductItem/>)
        })      
    }
    render(){
        const itemsRendered = this.state.productItemObjects.map((item, index) =>
                <Row>
                    <Col lg={{size : 2, order : 3,  offset : 1}} sm="6" sm={1} key={index} >
                        {this.state.productItemObjects[index]}
                    </Col>
                 </Row>)
        console.log(this.state.productItemObjects[1])
        return(
            <div style={{ justifyContent : "center"}}>  
                    {itemsRendered}
            </div>
        )
    }
}
export default ProductHandler
