import React, {Component} from 'react'
import RegisterForm from './forms/Register'

class PageHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            formPaths : ['/register'],
            viewForm : false
        }
        this.loadedForm = null
    }
    loadRequiredForm = () => {
        if(window.location.pathname == this.state.formPaths[0]){
            this.loadedForm = <RegisterForm/>
            this.setState({viewForm : true })
        }
    }
    render(){
        return(
            <React.Fragment onChange={this.loadRequiredForm}>
                {(this.state.viewForm) ? 
                this.loadedForm : ''}
            </React.Fragment>
        )
    }
}

export default PageHandler;
