import React, {Component} from 'react'

class FilterHandler  extends Component {
    constructor (props) {
        super(props)
        this.state ={ 
            symbol : "+",
            filteredProducts : [],
            minPrice : 0, 
            maxPrice : 0, 
            isFiltering : true
        }
    }
    handleFilter = (e) => {
        e.preventDefault()
        // this.setState({
        //     isFiltering : this.state.isFiltering ^= true,
        //     symbol : !this.state.isFiltering ? "+" : "-"
        // })
    }
    filterForm = () => {
        if (this.state.isFiltering){
            return (
                <div className="filter-wrapper">
                    <div className="price-filter-wrapper">
                       <h3 style={{textAlign : "left", fontSize : "17px"}}>Price</h3>
                       <label style={labelStyle}>
                       <input style={checkboxStyle} id="min-price-check-box" type="checkbox"/> 200$ - 300$
                       </label>
                    </div>
                </div>
            )
        }
        else
            return null
    }
    render () {
        return (
            <div style={{paddingRight : "70px", textAlign : "right"}} className="filter-wrapper">
                    <span style={{cursor : "pointer"}} onClick={this.handleFilter}>{this.state.symbol}Filter</span>
                    {this.filterForm()}
                </div>
        )
    }
}
const checkboxStyle = {
    width : "16px",
    height : "16px",
}
const labelStyle = {
    marginBottom : "10px"
}

export default FilterHandler
