import React, {Component} from "react"
import {
    Col
  } from 'reactstrap'

class ProductItem extends Component{
    constructor(props){
        super(props)
        this.state = {
            price : 40.00,
            image : "",
            color : "Black",
            title : "CRTee",
            id : "123AGDAS"
        }
    }
    setImage = (path) => {
        this.setState({image :  path})
    }
    render(){    
        return(
                <div onClick={this.props.handleItemClick()} className={this.state.id} style={itemStyle}>
                    <img style={imgStyle} src="/CRTee.png" alt="Failed to load"/>
                    <h5 style={{marginBottom : "-5px"}}>{this.state.title}</h5>
                    <p style={{marginBottom : "-5px", justifyContent : "center"}}>{this.state.color}</p>
                    <p style={{paddingTop : "-100px", justifyContent : "center"}}>AU${this.state.price}</p>
                </div>
        )
    }
}
const itemStyle = {
    width : "200px",
}
const imgStyle = {
    width : "200px",
}
export default ProductItem
