import React, {Component} from "react"

import ProductGrid from './ProductGrid'
import RenderFilter from "./RenderFilter"

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 0,
            selectedItem : "",
            productItemObjects : [],
            isFiltering : false,
            isCleared : false,
            isMaxChecked : false
        }
        this.COLUMN_COUNT = 4
        this.rowCount = Math.ceil(this.state.productCount / this.COLUMN_COUNT)
    }
    passFilterValue = () =>{
        this.setState({
            isFiltering : !this.state.isFiltering
        })
        console.log(this.state.isFiltering)
        return this.state.isFiltering
    }
    async componentDidMount() {
        const res = await fetch("http://127.0.0.1:8000/api/products/", {
                method : "GET",
                headers : {
                    "Content-Type" : "application/json",
                    'Accept': 'application/json'
                }
            })
        const data = await res.json()
        if (!this.state.isFiltering){
            this.setState({
                productCount : data["product_count"],
                productItemObjects : await  Promise.all(data["products"])
            })    
        }

    }

    handleSelectedItem = (itemSelected) => {
        this.setState({
            selectedIndex : itemSelected
        })
    }
    filterByMax = async () => {
        this.setState({
            isFiltering : true
        })
        let res = await fetch("http://127.0.0.1:8000/api/products_max", {
            method : "GET",
            headers : {
                "Content-Type" : "application/json",
                "Accept" : "application/json"
            }
        })
        let data = await res.json()
        this.setState({
            productItemObjects : data["products_max"],
            isFiltering : false        
        })
    }
    selectItem = (e) => {
        this.handleSelectedItem(e.target.id) 
        console.log(e.target.id)
    }
    render(){
        return(
            <div className="productWrapper"> 
                <RenderFilter itemsToFilter={this.state.productItemObjects}
                              isFiltering={this.passFilterValue} 
                              isMaxChecked={this.state.isMaxChecked}
                              toggleMaxCheckbox={this.toggleMaxCheckbox}
                              filterByMax={this.filterByMax}/>           
                <div className="gridContainer">
                    <ProductGrid rows={this.rowCount}
                                cols={this.COLUMN_COUNT}
                                itemWidth="200px" 
                                itemHeight="310px" 
                                itemsToRender={this.state.productItemObjects}
                                handleGridSelection={this.selectItem}
                                //isCleared={clearedGrid}
                                />
                </div>
            </div>
        )
    }
}
export default ProductHandler
