import React, {Component} from 'react'

import "../../checkbox.css"

class RenderFilter  extends Component {
    constructor (props) {
        super(props)
        this.state ={ 
            symbol : "+",
            isOpen : false,
            isMaxChecked : false
        }
        this.PRICE_THRESHOLDS = [0, 10, 20, 30, 40, 50, 100, 150, 200, 300, 400, 500, 700, 900, 1000]
    }
    getHighestPrice = () => {
        return Math.max(...this.props.itemsToFilter.map((item) => {return item.price}))
    }
    getLowestPrice = () => {
        return Math.min(...this.props.itemsToFilter.map(item => {return item.price}))   
    }
    handleSymbolChange = (e) => {
        e.preventDefault()     
        this.setState({
            symbol : this.props.isFiltering() ? "-" : "+",
            isOpen : true
        })   
    }
    handleFilterByMax = () => {
        this.props.filterByMax()
    }
    displayCheckboxText = (startPrice, endPrice) => {
        return "$" + startPrice + " - " + "$" + endPrice
    }
    getPriceDifferences = () => {
        var diff = []
        for (var i = 0; i < this.props.itemsToFilter.length; i++){  
            if(this.props.itemsToFilter[i] !== undefined || null){
                if (this.props.itemsToFilter[i + 1] !== undefined){
                    diff.push(Math.abs(this.props.itemsToFilter[i].price - this.props.itemsToFilter[i + 1].price))
                    
                }else{
                    break;    
                }                                    
            }
        }
        diff.sort((a, b) => a - b)
        return diff;
    }
    toggleMaxCheckbox = (e) => {
        console.log(this.state.isMaxChecked)
        this.setState({isMaxChecked : !this.state.isMaxChecked})
        this.handleFilterByMax()
    }
    renderCheckboxPrice = () => {
        let checkboxes = [] 
        for(let i = 0; i < this.PRICE_THRESHOLDS.length; i++){   
            console.log("PT: " + this.PRICE_THRESHOLDS[i] + " D: " + this.getPriceDifferences()[i])      
            if ( this.getPriceDifferences()[i] > this.PRICE_THRESHOLDS[i]){
                checkboxes.push(
                    <span onClick={this.toggleMaxCheckbox} onChange={this.handleFilterByMax}>            
                    <input 
                    className="max-price"
                    type="checkbox"
                    checked={this.state.isMaxChecked}
                    onChange={this.toggleMaxCheckbox}
                    /><span></span> {this.displayCheckboxText(this.sortedPriceArray()[i], this.sortedPriceArray()[i])}
                </span> 
                )
            }
        }
        console.log(checkboxes)
        return checkboxes;
    }
    sortedPriceArray = () => {
        let priceArray
        if (this.props.itemsToFilter !== undefined){
             priceArray = this.props.itemsToFilter.sort((a, b) => {
                return a.price - b.price;
            })
        }
        return priceArray
    }
    checkProductBetweenValues = (startValue, endValue) => {
        let productExists = false;
        for (let i = 0; i < this.props.itemsToFilter.length; i++){
            if (this.props.itemsToFilter[i] !== undefined){
                if ( this.props.itemsToFilter[i].price > startValue && this.props.itemsToFilter[i].price < endValue){
                    productExists = this.props.itemsToFilter.includes(this.props.itemsToFilter[i])
                }
            }
        }
        return productExists
    }
    filterForm = () => {
        if (this.state.isOpen){
            return (
                <div style={{display : "flex", flexDirection : "row", flexGrow : "5", flexWrap : "nowrap",  justifyContent : "flexStart"}} className="filter-wrapper">
                    <div style={{position : "relative",  marginLeft : "115vh"}} className="price-filter-wrapper">                   
                       <ul style={{listStyle : "none"}}>
                        <li>
                            <h3 style={{ textAlign : "left", fontSize : "17px"}}>Price </h3>
                        </li>        
                        {this.renderCheckboxPrice().map((item, i) => (
                            <div key={i}>{item}</div>
                        ))}       
                        <li>

                    </li>      
                       </ul>
                    </div>
                </div>
            )
        }
        else
            return null
    }
    render () {
        console.log(this.sortedPriceArray())
        return (
            <div style={{paddingRight : "70px", textAlign : "right"}} className="filter-wrapper">
                    <span style={{cursor : "pointer"}} onClick={this.handleSymbolChange}>{this.state.symbol}Filter</span>
                    {this.filterForm()}
                </div>
        )
    }
}


export default RenderFilter
