import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'
import ProductGrid from './ProductGrid'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 40,
            selectedItem : "",
            productItemObjects : []
        }
        this.COLUMN_COUNT = 4
        this.rowCount = Math.ceil(this.state.productCount / this.COLUMN_COUNT)
    }
    async componentDidMount() {
        const res = await fetch({
                method : "GET",
                headers : {
                    "Content-Type" : "application/json",
                    'Accept': 'application/json'
                }
            })
        const data = await res.json()
        const products = await data["products"]
        this.setState({
            productCount : data["product_count"],
            productItemObjects :  Array.from(Array(this.state.productCount), () => <ProductItem
                                                                                     itemCount={this.state.productCount} 
                                                                                     handleItemClick={this.selectItem}
                                                                                     title={products["title"]}
                                                                                     price={products["price"]}
                                                                                     id={products["product_id"]}
                                                                                     imagePath={products["image_path"]}/>)
        })          
    }
    handleSelectedItem = (itemSelected) => {
        this.setState({
            selectedIndex : itemSelected
        })
    }
    selectItem = (e) => {
        this.handleSelectedItem(e.target.id) 
    }
    render(){
        return(
            <div className="productListWrapper"> 
                <ProductGrid rows={this.rowCount}
                             cols={this.COLUMN_COUNT}
                             itemWidth="200px" 
                             itemHeight="310px" 
                             itemsToRender={this.state.productItemObjects}
                             />
            </div>
        )
    }
}
export default ProductHandler
