import React, {Component} from 'react'
import Navbar from './Navbar'
import {Link, BrowserRouter as Route} from 'react-router-dom'
import RegisterForm from '../forms/Register'

class Header extends Component{
    state = {
        error: null, 
        isLoaded: false,     
        currentPath: "" 
    }
    handleRegisterClick = (e) => {
        this.setState({
            currentPath : e.target.href
        })
        return (<RegisterForm/>)
    }
    setActivePath = (activePathName, comparedPathName) => {
        if (activePathName === comparedPathName){
            return {textDecoration: "none", color: "blue"}
        }else if(activePathName != comparedPathName){
            return {textDecoration: "none", color: "black"}
        };
    }
    render(){
        return(
            <div className="App">
                <h1 style={textStyle}>the outer layer of the cerebrum<br/>
                (the cerebral cortex ), composed <br/>
                of folded grey matter and playing<br/>               
                an important role in consciousness.</h1>
                <Route>
                    <Link to="/" onClick={this.handleClick}>
                        <img style={imageStyle} src="/test-static/headerimg.png" alt="Failed to load" />
                    </Link>
                </Route>
                <Navbar currentPath={this.state.currentPath} handleRegisterClick={this.handleRegisterClick} setActivePath={this.setActivePath}/>
            </div>
        )
    }
}

const textStyle = {
    textAlign: "left",
    verticalAlign: "middle",
    fontSize: "20px",
    padding: "30px",
    paddingLeft: "80px",
    paddingBottom: "35px",
    lineHeight: "32px"
 }
 const imageStyle = {
    position: "absolute",
    right: "0px",
    top: "0px",
    paddingRight: "80px",
    width: "250px",
    height: "250px"
 }

export default Header
