import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 40,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
    }
    componentDidMount(){
        this.setState({
            productItemObjects :  Array.from(Array(this.state.productCount), () => <ProductItem/>)
        })      
    }
    render(){
        const itemsRendered = this.state.productItemObjects.map((item, index) =>
                 <Col md={{size : 3, order : 10, offset : -10}} sm="6" sm={1} key={index} >{this.state.productItemObjects[index]}</Col>)
        console.log(this.state.productItemObjects[1])
        return(
            <div style={{paddingLeft : "20%", justifyContent : "center"}}> 
                <Row lg="7" style={{paddingRight : "10px"}}>   
                    {itemsRendered}
                </Row>  
            </div>
        )
    }
}
export default ProductHandler
