import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

class ProductItem extends Component{
    constructor(props){
        super(props)
        this.state = {
            price : 40.00,
            image : "",
            color : "Black",
            title : "CRTee"
        }
    }
    setImage = (path) => {
        this.setState({image :  path})
    }
    render(){
        this.setImage("/CRTee.png")
        return(
            <React.Fragment style={itemStyle}>
                <img style={imgStyle} src={this.state.image} alt="Failed to load"/>
                <h4>{this.state.title}</h4>
                <p>{this.state.color}</p>
                <p>AU${this.state.price}</p>
            </React.Fragment>
        )
    }
}
const itemStyle = {
    width : "200px",
    width : "200px",
}
const imgStyle = {
    width : "200px",
    width : "200px",
}
export default ProductItem
