import React, {Component} from 'react'
import {Link} from 'react-router-dom'
import Base from '../base/Base'
import Modal from 'react-modal'
import {
          Row, 
          Col,
        } from 'reactstrap'


Modal.defaultStyles.overlay.backgroundColor = "none"

class RegisterForm extends Component {
    constructor(props){
        super(props)
        this.state = {
            values: {
                email: "",
                firstname: "",
                lastname: "",
                fullName: "",
                password : "",
                passwordConfirm : "",
                phoneNumber : "",
            },
            isSubmitting: false,
            isOpen : false,
            isValid : false,
            isError: false,
            isHover : false
        } 

        this.status = null
    }

    validateForm = () => {
        if(this.status !== null){
            if (this.status == 406){
                this.isInvalid = true
            }else if(!this.isInvalid)
                this.isValid = true
        }
    }
    handleFormChange = (e) =>{
        let elementName = e.target.name;
        let content = e.target.value;
        this.setState({
            values : {
                ...this.state.values, [elementName] : content
            }            
        });
    }
    SubmitRegisterData = async e =>{
        e.preventDefault();
        console.log(this.state)
        if(this.state.isValid){
            this.setState({isSubmitting : true})

            const res = await fetch("http://127.0.0.1:8000/api/accounts/", {
                method : "POST",
                body: JSON.stringify(this.state.values),
                headers : {
                    "Content-Type" : "application/json",
                    'Accept': 'application/json'
                }
            });
            this.status = res.status
            this.setState({isSubmitting : false})
            const data = await res.json();
            !data.hasOwnProperty("error")
            ? this.setState({ message : data.success })
            : this.setState({message: data.error, isError: true}) 
            console.log(this.state.message)
            this.setState({
                values : {
                    email: "",
                    firstname: "",
                    lastname: "",
                    fullName: "",
                    password : "",
                    passwordConfirm : "",
                    phoneNumber : "",
                },
            })
        }   
    }
    feedbackStyle = () => {
        if(this.isInvalid == true){
            return {
                display : "flex"
            }
        }
    }
    toggleHover = (e) => {
        this.setState({isHover : !this.state.hover})
        if (this.state.isHover)
            this.setState({isHover : false})
    }
    getHoverStyle = () => {
        var style
        if(this.state.isHover)
            style = "#404040"
        else
            style = "black"
        return style
    }
    componentDidMount(){
        this.setState({
            isOpen : true
        });
    }
    validatePassword = () => {
        if (this.state.values.password !== this.state.values.confirmPassword){
            this.setState({isValid : false})
            return "red"
        }
        else {
            this.setState({isValid : true})
        }
    }
    render(){
        this.validateForm()
        const buttonStyle = {
            outline : "none",
            width: "90%",
            height : "39px",
            marginTop : "30px", 
            marginLeft : "18px",
            borderRadius : "10px",
            borderColor : "	#FFFFFF",
            borderWidth : "2px",
            backgroundColor : this.getHoverStyle(),
            color : "white",
            transition: "all ease .5s",
        }
        const inputStyle = {
            borderColor : this.validatePassword(),
            width : "100%"
        }
        document.getElementById('root').style.filter = 'none'
        return(    
            <div style={{display : "flex"}}>          
                <Modal isOpen={this.state.isOpen} ariaHideApp={false} style={modalStyle} width="10">
                    <h2>Sign Up</h2>
                    <hr/>
                    <Row style={{paddingTop : "10px"}}>             
                        <Col>
                            <label style={{display : "flex"}}>First Name</label>
                            <input style={{width: "100%", paddingRight : "5px" }} type="text" name="firstname" placeholder="Enter your first name"/>
                        </Col>
                        <Col style={{paddingLeft : "5px"}}>
                            <label style={{display : "flex"}}>Last Name</label>
                            <input style={{width: "100%" }} type="text" name="lastname" placeholder="Enter your last name"/>
                        </Col>
                    </Row>
                    <Row style={{paddingTop : "10px"}}>
                        <Col>
                            <label style={{display : "flex"}}>Email</label>
                            <input style={inputStyle} type="text" name="firstname" placeholder="Enter your email"/>
                            <label style={{display : "flex", paddingTop : "10px"}}>Password</label>
                            <input style={inputStyle} type="password" name="password" onChange={this.validatePassword}  placeholder="Enter your password"/>
                            <label style={{display : "flex", paddingTop : "10px"}}>Confirm Password</label>
                            <input style={inputStyle} type="password" name="password"  onChange={this.validatePassword} placeholder="Confirm password"/>
                            <button className="registerSubmit" onMouseEnter={this.toggleHover} onMouseLeave={this.toggleHover} style={buttonStyle} type="submit" onSubmit={this.SubmitRegisterData}>
                                Create a Cortex account
                            </button>
                        </Col>
                    </Row>
                </Modal>
            </div>
        )
    }
}
const formStyle = {
    padding: "15px",
    margin: "auto",
    width : "100%",
    maxWidth : "1000px"
}
const modalStyle = {
    content : {
        margin : "auto",
        width : "55vh",
        height : "51vh",
        position : "absolute",
        borderRadius : "10px",
        overflow : "none",
        boxShadow : "0px 0px 20px"
      }
}
export default RegisterForm
