import React, {Component} from "react"

import ProductItem from './ProductItem'

class ProductGrid extends Component {
    constructor(props){
        super(props)
        this.state = {
            cell : null
        }
    }
    makeGrid = () => {
        let rows = this.props.rows
        let cols = this.props.cols
        let grid = []
        let cell
        for (let c = 0; c < (rows * cols); c++){
            cell = <div key={c}>{this.props.itemsToRender[c]}</div>
            grid.push(cell)
        }
        return grid
    }
    setCellHeight = () => {
        let propHeight = this.props.itemHeight.concat(" ")
        let height = propHeight.repeat(this.props.rows)
        return height
    }
    setCellWidth = () => {
        let propWidth = this.props.itemWidth.concat(" ")
        let width = propWidth.repeat(this.props.cols)
        return width
    }
    selectItem = (e) => {
        this.props.handleSelection(e.target.key)
    }
    mapKeyToItem = () => {
        this.props.itemsToRender.map((item, i) => (
            <span key={i}>{item}</span>
        ))
    }
    render(){
        const containerStyle = {
            display : "grid",
            justifyContent : "center",
            gridRows : this.props.rows,
            gridCols : this.props.cols,
            gridTemplateRows : this.setCellHeight(),
            gridTemplateColumns : this.setCellWidth(),
            columnGap : "20px",
            rowGap : "20px"
          
        }
        return(
            <div onClick={this.selectItem} style={containerStyle} id="container">
                {this.makeGrid()}
                {this.mapKeyToItem()}
            </div>
        )
    }
}
export default ProductGrid
