import React, {Component} from 'react'
import Navbar from './Navbar'
import {useLocation} from "react-router-dom"
class Header extends Component{
    setActivePath = (activePathName) => {
        let location = useLocation()
        if (activePathName == location){
            console.log(window.location.pathname)
            return {textDecoration: "none", color: "blue"}
        }else{
            return {textDecoration: "none", color: "black"}
        };
      }
    render(){
        return(
            <div className="App">
                <h1 style={textStyle}>the outer layer of the cerebrum<br/>
                (the cerebral cortex ), composed <br/>
                of folded grey matter and playing<br/>               
                an important role in consciousness.</h1>
                <img style={imageStyle} src="/test-static/headerimg.png" alt="Failed to load"/>
                <Navbar setActivePath={this.setActivePath}/>
            </div>
        )
    }
}

const textStyle = {
    textAlign: "left",
    verticalAlign: "middle",
    fontSize: "20px",
    padding: "30px",
    paddingLeft: "80px",
    paddingBottom: "35px",
    lineHeight: "32px"
 }
 const imageStyle = {
    position: "absolute",
    right: "0px",
    top: "0px",
    paddingRight: "80px",
    width: "250px",
    height: "250px"
 }

export default Header
