import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 40,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
    }
    componentDidMount(){
        this.setState({
            productItemObjects :  Array.from(Array(this.state.productCount), () => <ProductItem itemsRendered={this.productCount}/>)
        })  
          
    }
    gridContent = () => {
        let content = []
        this.state.productItemObjects.forEach((product, index) => {
            if((index + 1) % 4 == 0){
                content.push(
                    <Row md="3">
                        <ProductItem/>
                    </Row>
                )
            }else{
                content.push(
                    <Col md="3">
                        <ProductItem/>
                    </Col>
                )
            }
        });
        return(
            <div>
                {content}
            </div>
        )
    }
    render(){
        const rowItemLimit = 4;
        const itemsRendered = this.state.productItemObjects.map((item, index) =>                   
                <div>{this.state.productItemObjects[index]}</div>
        )
        console.log(this.state.productItemObjects[1])
        return(
            <div style={{ justifyContent : "center"}}> 
                {this.gridContent()}
            </div>
        )
    }
}
export default ProductHandler
