import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 0,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
    }
    setProductItems = () =>{
        this.setState({productItemObjects : [<ProductItem/>]})
    }
    render(){
        this.setProductItems()
        return(
            <React.Fragment>
                {this.state.productItemObjects.map((item, i) => {
                    return <ProductItem key={i} itemsRendered={this.state.productCount}/>
                })}            
            </React.Fragment>
        )
    }
}
export default ProductHandler
