import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 2,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
    }
    componentDidMount(){
        for (let i = 0; i < this.state.productCount; i++){
            this.setState({
                productItemObjects : [<ProductItem/>]
            })
        }
    }
    render(){
        const itemsRendered = this.state.productItemObjects.map((item, index) =>
                 <div key={index}>{this.state.productItemObjects[index]}</div>)
        console.log(this.state.productItemObjects[0])
        return(
            <div> 
                {itemsRendered}        
            </div>
        )
    }
}
export default ProductHandler
