import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'
import ProductGrid from './ProductGrid'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 40,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
    }
    componentDidMount(){
        this.setState({
            productItemObjects :  Array.from(Array(this.state.productCount), () => <ProductItem itemsRendered={this.productCount}/>)
        })          
    }
    render(){
        return(
            <div className="productListWrapper"> 
                <ProductGrid rows={6} cols={4} itemWidth="200px " itemHeight="200px "/>
            </div>
        )
    }
}
export default ProductHandler
