import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 40,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
        this.limitCol = null
    }
    componentDidMount(){
        this.setState({
            productItemObjects :  Array.from(Array(this.state.productCount), () => <ProductItem/>)
        })      
    }
    setColLimit = (index, limit) => {
        this.limit = <Col>{this.state.productItemObjects[index]}</Col>
        if (index == limit){
            limit = limit + limit 
            this.limitCol = <Col>{this.state.productItemObjects[index]}</Col>
        }
    }
    render(){
        const rowItemLimit = 4;
        const itemsRendered = this.state.productItemObjects.map((item, index) => {
                   this.setColLimit(index, 4)
                   this.limitCol
        });
        console.log(this.state.productItemObjects[1])
        return(
            <div style={{ justifyContent : "center"}}> 
                <Row style={{paddingRight : "10px"}}>   
                    {itemsRendered}
                </Row>  
            </div>
        )
    }
}
export default ProductHandler
