import React, {Component} from "react"

import ProductItem from './ProductItem'

class ProductGrid extends Component {
    constructor(props){
        super(props)
        this.state = {
            cell : null
        }
    }
    makeRows = () => {
        let rows = this.props.rows
        let cols = this.props.cols
        let grid = []
        for (let c = 0; c < (rows * cols); c++){
            let cell = <ProductItem key={c}/>
            grid.push(cell)
        }
        return grid
    }
    setCellWidth = () => {
        let width 
        for (let w = 0; w < this.props.rows; w++){
            width = "".concat(...["200px "])        
        }
        return width
    }
    setCellHeight = () => {
        let height
        for (let h = 0; h < this.props.cols; h++){     
            height = "".concat("200px ") 
        }
        console.log(height)
        return height
    }
    render(){
        const containerStyle = {
            display : "grid",
            justifyContent : "center",
            gridRows : this.props.rows,
            gridCols : this.props.cols,
            gridTemplateRows : this.setCellWidth(),
            gridTemplateColumns : this.setCellHeight(),
            columnGap : "10px",
            rowGap : "100px"
          
        }
        return(
            <div style={containerStyle} id="container">
                {this.makeRows()}
            </div>
        )
    }
}
export default ProductGrid
