import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'
import ProductGrid from './ProductGrid'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 40,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
        this.COLUMN_COUNT = 4
        this.rowCount = Math.ceil(this.state.productCount / this.COLUMN_COUNT)
    }
    componentDidMount(){
        this.setState({
            productItemObjects :  Array.from(Array(this.state.productCount), () => <ProductItem itemCount={this.state.productCount} handleItemClick={this.selectItem}/>)
        })          
    }
    handleSelectedItem = (itemSelected) => {
        this.setState({
            selectedIndex : itemSelected
        })
    }
    selectItem = (e) => {
        this.props.handleSelection(e.target.id)
        console.log(e.target.id)
    }
    render(){
        return(
            <div className="productListWrapper"> 
                <ProductGrid rows={this.rowCount}
                             cols={this.COLUMN_COUNT}
                             itemWidth="200px" 
                             itemHeight="310px" 
                             itemsToRender={this.state.productItemObjects}
                             handleSelection={this.handleSelectedItem}
                             />
            </div>
        )
    }
}
export default ProductHandler
