import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

class ProductItem extends Component{
    constructor(props){
        super(props)
        this.state = {
            price : 40.00,
            image : "",
            color : "Black",
            title : "CRTee"
        }
    }
    setImage = (path) => {
        this.setState({image :  path})
    }
    render(){    
        return(
            <div style={itemStyle}>
                <img style={imgStyle} src="/CRTee.png" alt="Failed to load"/>
                <h5 style={{marginBottom : "-5px"}}>{this.state.title}</h5>
                <p style={{marginBottom : "-5px"}}>{this.state.color}</p>
                <p style={{paddingTop : "-100px"}}>AU${this.state.price}</p>
            </div>
        )
    }
}
const itemStyle = {
    width : "150px",
    width : "150px",
    paddingRight : "100px"
}
const imgStyle = {
    width : "150px",
    width : "150px",
}
export default ProductItem
