import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 40,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
    }
    componentDidMount(){
        this.setState({
            productItemObjects :  Array.from(Array(this.state.productCount), () => <ProductItem itemsRendered={this.productCount}/>)
        })      
    }
    render(){
        const rowItemLimit = 4;
        const itemsRendered = this.state.productItemObjects.map((item, index) =>                   
                <div>{this.state.productItemObjects[index]}</div>
        )
        console.log(this.state.productItemObjects[1])
        return(
            <div style={{ justifyContent : "center"}}> 
                <Row md="4" style={{ justifyContent : "center"}}>   
                    <Col sm="2">
                    <ProductItem/>
                    <ProductItem/>           
                    </Col>
                    <Col sm="2">
                    <ProductItem/>
                    <ProductItem/>
                    <ProductItem/>    
                    </Col>
                    <Col sm="2">
                    <ProductItem/>
                    <ProductItem/>
                    <ProductItem/>    
                    </Col>
                </Row>  
            </div>
        )
    }
}
export default ProductHandler
