import React, {Component} from 'react'

import Checkbox from "../Checkbox"
class FilterHandler  extends Component {
    constructor (props) {
        super(props)
        this.state ={ 
            symbol : "+",
            filteredProducts : [],
            minPrice : 0, 
            maxPrice : 0, 
            isFiltering : true
        }
    }
    filterByPrice = () =>{

    }
    getHighestPrice = () => {
       console.log(Math.max(...this.props.itemsToFilter.map((item) => {return item.price})))
       return Math.max(...this.props.itemsToFilter.map((item) => {return item.price}))
    }
    getLowestPrice = () => {
        return Math.min(...this.props.itemsToFilter.map(item => {return item.price}))
    }
    handleFilter = (e) => {
        e.preventDefault()
        // this.setState({
        //     isFiltering : this.state.isFiltering ^= true,
        //     symbol : !this.state.isFiltering ? "+" : "-"
        // })
    }
    filterForm = () => {
        if (this.state.isFiltering){
            return (
                <div style={{display : "flex", flexDirection : "row", flexGrow : "5", flexWrap : "nowrap",  justifyContent : "flexStart"}} className="filter-wrapper">
                    <div style={{position : "relative",  marginLeft : "115vh"}} className="price-filter-wrapper">                   
                       <ul style={{listStyle : "none"}}>
                        <li>
                            <h3 style={{ textAlign : "left", fontSize : "17px"}}>Price </h3>
                        </li>
                        <li>
                            <div>                           
                                <Checkbox/> ${this.getHighestPrice()} - $50                          
                            </div>
                        </li>
                       </ul>
                    </div>
                    <div>                   
                    <ul style={{listStyle : "none"}}>
                     <li>
                         <h3 style={{ textAlign : "left",  fontSize : "17px"}}>Price </h3>
                     </li>
                     <li>
                         <div>                           
                             <Checkbox/> $100 - $50                          
                         </div>
                     </li>
                    </ul>
                 </div>
                </div>
            )
        }
        else
            return null
    }
    render () {
        return (
            <div style={{paddingRight : "70px", textAlign : "right"}} className="filter-wrapper">
                    <span style={{cursor : "pointer"}} onClick={this.handleFilter}>{this.state.symbol}Filter</span>
                    {this.filterForm()}
                </div>
        )
    }
}

const checkboxStyle = {
    width : "16px",
    height : "16px",
}
const labelStyle = {
}

export default FilterHandler
