        let rows = this.props.rows
        let cols = this.props.cols
        let grid = []
        for (let c = 0; c < (rows * cols); c++){
            let cell = <div>Hello</div>
            grid.push(cell * rows)
        }
        return grid
    }
    render(){
        const containerStyle = {
            display : "grid",
            gridRows : this.props.rows,
            gridCols : this.props.cols,
            gridTemplateRows : "repeat(var(gridRow), 1fr)",
            gridTemplateColumns : "repeat(var(gridCol), 1fr)",
          
        }
        return(
            <div style={containerStyle} id="container">
                {this.makeRows()}
            </div>
        )
    }
}
