import React, {Component} from 'react'

class FilterHandler  extends Component {
    constructor (props) {
        super(props)
        this.state ={ 
            symbol : "+",
            filteredProducts : [],
            minPrice : 0, 
            maxPrice : 0, 
            isFiltering : true
        }
    }
    handleFilter = (e) => {
        e.preventDefault()
        // this.setState({
        //     isFiltering : this.state.isFiltering ^= true,
        //     symbol : !this.state.isFiltering ? "+" : "-"
        // })
    }
    filterForm = () => {
        if (this.state.isFiltering){
            return (
                <React.Fragment>
                    <ul  style={{justifyContent : "left"}}>
                       <p>Price</p>
                       <input style={{marginRight : "14px"}} type="checkbox"/>
                    </ul>
                </React.Fragment>
            )
        }
        else
            return null
    }
    render () {
        return (
            <div style={{paddingRight : "70px", textAlign : "right"}} className="filter-wrapper">
                    <span style={{cursor : "pointer"}} onClick={this.handleFilter}>{this.state.symbol}Filter</span>
                    {this.filterForm()}
                </div>
        )
    }
}

export default FilterHandler
