import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 2,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
    }
    componentDidMount(){
        for (let i = 0; i < this.state.productCount; i++){
            this.state.productItemObjects.push(<ProductItem/>)
        }
    }
    render(){
        
        return(
            <React.Fragment>
                {this.state.productItemObjects.map((item, i) => {
                     return <div>{item[i]}</div>
                })}            
            </React.Fragment>
        )
    }
}
export default ProductHandler
