import React, { Component } from 'react';
import axios from "axios"
import Header from "./components/base/Header"
import Bottom from "./components/base/Bottom"
import {BrowserRouter as Route} from 'react-router-dom'
import './App.css';

class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      description: ""
    }
  }
  setActivePath = (activePathName) => {
    if (activePathName == window.location.pathname){
        return "blue"
    }else{
        return "black"
    };
  }
  render(){
    // axios.get('http://127.0.0.1:8000/api/products/')
    // .then(response => this.setState({
    //   description : response.data[0].description
    // }))
    // .catch(err => console.log(err));  
    // axios.get('http://127.0.0.1:8000/api/products/')
    // .then(response => {
    //   console.log(response);
    // })
    // .catch(err => console.log(err));  
    // console.log(this.state.description)
    return (
      <div style={pageStyle} className="pageContainer"> 
        <div style={contentStyle} className="contentWrapper">
          <Route>
            <Header setActivePath={this.setActivePath}/>
          </Route>
        </div>
        <Bottom/>
      </div>
    );
  }
}

const pageStyle = {
  margin: "0",
  display: "flex",
  justifyContent: "space-between",
  padding: "0%",
  float: "bottom",
  flexDirection: "column",
  minHeight: "100vh",
  textAlign: "center",
}
const contentStyle = {
  flex : "1 1 100%",
}

export default App;
