import React, {Component} from 'react'

class FilterHandler  extends Component {
    constructor (props) {
        super(props)
        this.state ={ 
            symbol : "+",
            filteredProducts : [],
            minPrice : 0, 
            maxPrice : 0, 
            isFiltering : false
        }
    }
    handleFilter = (e) => {
        e.preventDefault()
        this.setState({
            symbol : this.isFiltering ? true : "-",
            isFiltering : !(this.state.isFiltering != false)
        })
    }
    filterForm = () => {
        if (this.state.isFiltering){
            return (
                <React.Fragment>
                    <ul>
                        <li>Price </li>
                    </ul>
                </React.Fragment>
            )
        }
        else
            return null
    }
    render () {
        return (
            <div style={{paddingRight : "70px", textAlign : "right"}} className="filter-wrapper">
                    <span onClick={this.handleFilter}>{this.state.symbol}Filter</span>
                    {this.filterForm()}
                </div>
        )
    }
}

export default FilterHandler
