import React, {Component} from 'react'
import {Link} from 'react-router-dom'

import {
          Form, 
          FormGroup,
          Input,
          Label,
          Row, 
          Col,
          Container,
          Button,
          FormFeedback
        } from 'reactstrap'

class RegisterForm extends Component {
    constructor(props){
        super(props)
        this.state = {
            values: {
                email: "",
                firstname: "",
                lastname: "",
                fullName: "",
                password : "",
                passwordConfirm : "",
                phoneNumber : "",
            },
            isSubmitting: false,
            isError: false
        }     
        this.isInvalid = false
        this.comparedEmails = null
        this.status = null
    }

    validateForm = () => {
        if (this.status == 406){
            this.isInvalid = true
        }else 
            this.isInvalid = false
    }
    handleFormChange = (e) =>{
        let elementName = e.target.name;
        let content = e.target.value;
        this.setState({
            values : {
                ...this.state.values, [elementName] : content
            }            
        });
    }
    SubmitRegisterData = async e =>{
        e.preventDefault();
        console.log(this.state)
        this.setState({isSubmitting : true})

        const res = await fetch("http://127.0.0.1:8000/api/accounts/", {
            method : "POST",
            body: JSON.stringify(this.state.values),
            headers : {
                "Content-Type" : "application/json",
                'Accept': 'application/json'
            }
        });
        this.status = res.status
        this.setState({isSubmitting : false})
        const data = await res.json();
        !data.hasOwnProperty("error")
        ? this.setState({ message : data.success })
        : this.setState({message: data.error, isError: true}) 
        console.log(this.state.message)
        this.setState({
            values : {
                email: "",
                firstname: "",
                lastname: "",
                fullName: "",
                password : "",
                passwordConfirm : "",
                phoneNumber : "",
            },
        })
    }
    render(){
        this.validateForm()
        return(    
            <Container>
                <h3 style={{textAlign : "left"}}>Registration Form</h3>
                <Form style={formStyle}>               
                    <Row form>
                        <Col>
                            <FormGroup>
                                <Label style={{display : "flex"}}>First Name</Label>
                                <Input onChange={this.handleFormChange} name="firstname" style={{display : "flex"}}  type="firstname" placeholder="Enter your first name"/>
                            </FormGroup>
                        </Col>
                        <Col>
                            <FormGroup>
                                <Label style={{display : "flex"}}>Last Name</Label>
                                <Input onChange={this.handleFormChange} name="lastname" type="lastname" placeholder="Enter your last name"/>  
                            </FormGroup>
                        </Col>
                    </Row>
                    <FormGroup>
                        <Label style={{display : "flex"}}>Email</Label>
                        <Input invalid={this.isInvalid} onChange={this.handleFormChange} name="email" type="text" placeholder="Enter email"/>              
                        <FormFeedback >
                            You're already registered! Login? 
                        </FormFeedback>
                        <Label style={{display : "flex"}} >Password</Label>
                        <Input onChange={this.handleFormChange} name="password" type="password" placeholder="Enter password"/>  
                        <Label style={{display : "flex"}} >Confirm Password</Label>
                        <Input onChange={this.handleFormChange} name="passwordConfirm" type="password" placeholder="Please confirm your password"/>  
                    </FormGroup>    
                        <Button style={{width : "300px"}} onClick={this.SubmitRegisterData} type="submit">
                            Submit
                        </Button>
                </Form>
            </Container>  
        )
    }
}
const formStyle = {
    padding: "15px",
    margin: "auto",
    width : "100%",
    maxWidth : "1000px"
}
export default RegisterForm
