import React, { Component } from 'react';
import axios from "axios"
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import PageRouter from '../PageRoutes'
import Header from "./Header"
import Bottom from "./Bottom"

const ComponentLoader = ({match}) => console.log(match.params) || (
    <PageRouter UrlMatch={match.params.id}/>
)

class Base extends Component {
  constructor(props){
    super(props)
    this.state = {
      description: "",
      loadedComponent: null
    }
  }

  render(){
    return (
      <div className="Base">
        <Router>
        <div style={pageStyle}>
            <div style={contentStyle} className="baseContainer"> 
                <Header/>    
                <Switch>
                    <Route path="/:id" component={ComponentLoader} render={() => {
                        this.setState({
                            loadedComponent : ComponentLoader
                        })
                    }}/>
                </Switch>
            </div>
            <Bottom/>    
        </div>   
        </Router>
      </div>
    );
  }
}

const pageStyle = {
  margin: "0",
  display: "flex",
  justifyContent: "space-between",
  padding: "0%",
  float: "bottom",
  flexDirection: "column",
  minHeight: "100vh",
  textAlign: "center",
}
const contentStyle = {
  flex : "1 1 100%",
}

export default Base;
