import React, {Component} from 'react'
import {Link} from 'react-router-dom'
import Base from '../base/Base'
import Modal from 'react-modal'
import {
          Row, 
          Col,
        } from 'reactstrap'


Modal.defaultStyles.overlay.backgroundColor = "none"

class RegisterForm extends Component {
    constructor(props){
        super(props)
        this.state = {
            values: {
                email: "",
                firstname: "",
                lastname: "",
                fullName: "",
                password : "",
                passwordConfirm : "",
                phoneNumber : "",
            },
            isSubmitting: false,
            isOpen : false,
            isPasswordValid : false,
            isEmailValid : false,
            isValid : [this.isPasswordValid, this.isEmailValid],
            isError: false,
            isHover : false
        } 

        this.feedbackValue = null
        this.status = null
    }

    validateForm = () => {
        if(this.status !== null){
            if (this.status == 406){
                this.isInvalid = true
            }else if(!this.isInvalid)
                this.isValid = true
        }
    }
    handleFormChange = (e) =>{
        let elementName = e.target.name;
        let content = e.target.value;
        this.setState({
            values : {
                ...this.state.values, [elementName] : content
            }            
        });
    }
    SubmitRegisterData = async e =>{
        e.preventDefault();
        console.log(this.state)
        if(this.state.isValid[0] && this.state.isValid[1]){
            this.setState({isSubmitting : true})

            const res = await fetch("http://127.0.0.1:8000/api/accounts/", {
                method : "POST",
                body: JSON.stringify(this.state.values),
                headers : {
                    "Content-Type" : "application/json",
                    'Accept': 'application/json'
                }
            });
            this.status = res.status
            this.setState({isSubmitting : false})
            const data = await res.json();
            !data.hasOwnProperty("error")
            ? this.setState({ message : data.success })
            : this.setState({message: data.error, isError: true}) 
            console.log(this.state.message)
            this.setState({
                values : {
                    email: "",
                    firstname: "",
                    lastname: "",
                    fullName: "",
                    password : "",
                    passwordConfirm : "",
                    phoneNumber : "",
                },
            })
        }   
    }
    feedbackStyle = () => {
        if(this.isInvalid == true){
            return {
                display : "flex"
            }
        }
    }
    toggleHover = (e) => {
        this.setState({isHover : !this.state.hover})
        if (this.state.isHover)
            this.setState({isHover : false})
    }
    getHoverStyle = () => {
        var style
        if(this.state.isHover)
            style = "#404040"
        else
            style = "black"
        return style
    }
    componentDidMount(){
        this.setState({
            isOpen : true
        });
    }
    handlePassword = (e) => {
        this.setState({
            password : e.target.value
        })
    }
    validateEmail = (e) => {     
        let reEmail = new RegExp("/\S+@\S+\.\S+/")
        if(e.target.value != null){
            if (this.state.values.email.indexOf('@') > 0){
                this.feedbackValue = <p style={{color : "red", marginBottom : "-5px"}}>This is not a email</p>
                this.setState({
                isEmailValid : false
                })
            }
            else{
                this.setState({
                    isEmailValid : true
                })
            }
        }
    }
    validatePassword = (e) => {
        let passReg = new RegExp("^(?=(.*[a-zA-Z]){1,})(?=(.*[0-9]){2,}).{8}$");      
        if (e.target.value !== this.state.password){
            this.setState({
                isPasswordValid : false
            })
            this.feedbackValue = <p style={{color : "red", marginBottom : "-5px"}}>Passwords must match!</p>
        }  else {
            this.feedbackValue = null
            this.setState({
                isPasswordValid : true
            })
        }
        if (!passReg.test(this.state.confirmPassword)){
            this.setState({
                isPasswordValid : false
            })
            this.feedbackValue = <p style={{color : "red", marginBottom : "-5px"}}>Password must contain atleast 2 numbers 8 digits.</p>
        }else{
            this.setState({
                isPasswordValid : true
            })
            this.feedbackValue = null
        }
        
    }
    getPasswordInputStyle = () => {
        if (!this.state.isPasswordValid){
            return "red"
        }
        else {
            return "black"
        }
    }
    render(){
        this.validateForm()
        const buttonStyle = {
            outline : "none",
            width: "90%",
            height : "39px",
            marginLeft : "18px",
            borderRadius : "10px",
            borderColor : "	#FFFFFF",
            borderWidth : "2px",
            backgroundColor : this.getHoverStyle(),
            color : "white",
            transition: "all ease .5s",
        }
        const inputStyle = {
            borderColor : "black",
            borderWidth : "1px",
            borderRadius : "3px",
            outline: "none",
            width : "100%"
        }
        document.getElementById('root').style.filter = 'none'
        return(    
            <div style={{display : "flex"}}>          
                <Modal isOpen={this.state.isOpen} ariaHideApp={false} style={modalStyle} width="10">
                    <h2>Sign Up</h2>
                    <hr/>
                    <Row style={{paddingTop : "10px"}}>             
                        <Col>
                            <label style={{display : "flex"}}>First Name</label>
                            <input style={{width: "100%", paddingRight : "5px" }} type="text" name="firstname" placeholder="Enter your first name"/>
                        </Col>
                        <Col style={{paddingLeft : "5px"}}>
                            <label style={{display : "flex"}}>Last Name</label>
                            <input style={{width: "100%" }} type="text" name="lastname" placeholder="Enter your last name"/>
                        </Col>
                    </Row>
                    <Row style={{paddingTop : "10px"}}>
                        <Col>
                            <label style={{display : "flex"}}>Email *</label>
                            <input style={inputStyle} type="text" name="email" onChange={this.validateEmail} placeholder="Enter your email"/>
                            <label style={{display : "flex", paddingTop : "10px"}}>Password *</label>
                            <input style={inputStyle} type="password" name="password" onChange={this.handlePassword}  placeholder="Enter your password"/>
                            <label style={{display : "flex", paddingTop : "10px"}}>Confirm Password *</label>
                            <input autoFocus={false} style={inputStyle} type="password" name="confrimPassword"  onChange={this.validatePassword} placeholder="Confirm password"/>
                            {this.feedbackValue}
                            <label style={{ paddingLeft : "20px", color: "#808080", fontSize : "15px"}}>Protect your data!</label>                           
                            <ul style={{marginTop : "-10px", fontSize : "15px", color: "#808080"}}>
                                <li>Try to include one symbol in your password. !@#$</li>
                                <li>Password must have numbers.</li>
                            </ul>
                            <button className="registerSubmit" onMouseEnter={this.toggleHover} onMouseLeave={this.toggleHover} style={buttonStyle} type="submit" onSubmit={this.SubmitRegisterData}>
                                Create a Cortex account
                            </button>
                        </Col>
                    </Row>
                </Modal>
            </div>
        )
    }
}
const formStyle = {
    padding: "15px",
    margin: "auto",
    width : "100%",
    maxWidth : "1000px"
}
const modalStyle = {
    content : {
        margin : "auto",
        width : "55vh",
        height : "56vh",
        position : "absolute",
        borderRadius : "10px",
        overflow : "none",
        boxShadow : "0px 0px 20px"
      }
}
export default RegisterForm
