import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'
import ProductGrid from './ProductGrid'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 0,
            selectedItem : "",
            productItemObjects : [],
            testArray : [0, 1, 2, 3, 4, 5],
            values : {
                title : "",
                price : 0,
                color : "black",
                productId : "",
                imagePath : ""
            }
        }
        this.COLUMN_COUNT = 4
        this.rowCount = Math.ceil(this.state.productCount / this.COLUMN_COUNT)
    }
    async componentDidMount() {
        const res = await fetch("http://127.0.0.1:8000/api/products/", {
                method : "GET",
                headers : {
                    "Content-Type" : "application/json",
                    'Accept': 'application/json'
                }
            })
        const data = await res.json()
        const products = data["products"]
        this.setState({
            productCount : data["product_count"]
        }) 
        setValues()
        for(let i = 0; i < this.state.productCount; i++){
           this.setValues(products[i]["title"], products[i]["price"], "Black", products[i]["product_id"], products[i]["image_path"])
        }
    }
    setValues = (title, price, color, id, i_path) =>{
        this.setState({
            values : {
                title : title,
                price : price,
                color : color,
                productId : id,
                imagePath : i_path
            }
        })
    }
    handleSelectedItem = (itemSelected) => {
        this.setState({
            selectedIndex : itemSelected
        })
    }
    selectItem = (e) => {
        this.handleSelectedItem(e.target.id) 
    }
    render(){
        console.log(this.state.testArray)
        return(
            <div className="productListWrapper"> 
                <ProductGrid rows={this.rowCount}
                             cols={this.COLUMN_COUNT}
                             itemWidth="200px" 
                             itemHeight="310px" 
                             itemsToRender={this.state.productItemObjects}
                             />
            </div>
        )
    }
}
export default ProductHandler
