import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 5,
            selectedIndex : 0,
            productItem : null, 
            productItemObjects : []
        }
    }
    componentDidMount(){
        this.setState({
            productItemObjects :  Array.from(Array(this.state.productItemCount), () => <ProductItem/>)
        })      
    }
    render(){
        const itemsRendered = this.state.productItemObjects.map((item, index) =>
                 <div key={index}>{this.state.productItemObjects[index]}</div>)
        console.log(this.state.productItemObjects[1])
        return(
            <div> 
                {itemsRendered}        
            </div>
        )
    }
}
export default ProductHandler
