import React, {Component} from "react"
import {
    Row, 
    Col,
  } from 'reactstrap'

import ProductItem from './ProductItem'
import ProductGrid from './ProductGrid'

class ProductHandler extends Component{
    constructor(props){
        super(props)
        this.state = {
            productImages : [],
            productCount : 0,
            selectedItem : "",
            productItemObjects : Array(this.state.productCount)
        }
        this.COLUMN_COUNT = 4
        this.rowCount = Math.ceil(this.state.productCount / this.COLUMN_COUNT)
    }
    async componentDidMount() {
        const res = await fetch("http://127.0.0.1:8000/api/products/", {
                method : "GET",
                headers : {
                    "Content-Type" : "application/json",
                    'Accept': 'application/json'
                }
            })
        const data = await res.json()
        const products = data["products"]
        this.setState({
            productCount : data["product_count"],
        }) 
        // map products loop through    
        this.state.productItemObjects.fill((index) => (
            <ProductItem
            handleItemClick={this.selectItem}
            title={products[index]["title"]}
            price={products[index]["price"]}
            productId={products[index]["product_id"]}
            imagePath={products[index]["image_path"]}/>
        ) || console.log(index))     
    }
    handleSelectedItem = (itemSelected) => {
        this.setState({
            selectedIndex : itemSelected
        })
    }
    selectItem = (e) => {
        this.handleSelectedItem(e.target.id) 
    }
    render(){
        return(
            <div className="productListWrapper"> 
                <ProductGrid rows={this.rowCount}
                             cols={this.COLUMN_COUNT}
                             itemWidth="200px" 
                             itemHeight="310px" 
                             itemsToRender={this.state.productItemObjects}
                             />
            </div>
        )
    }
}
export default ProductHandler
